function shuffle(e){let n=e.length;for(;n;){let a=Math.floor(Math.random()*n--);[e[a],e[n]]=[e[n],e[a]]}}function renderlink(e){var n,a,r,t="";shuffle(e);for(var l=0;l<e.length;l++)n=e[l].name,a=e[l].avatar,r=e[l].site,t+='<div class="card"><a href="'+r+'" target="_blank"><div class="thumb" style="background: url( '+a+');"></div></a><div class="card-header"><div><a href="'+r+'" target="_blank">'+n+"</a></div></div></div>";document.querySelector(".link-navigation").innerHTML=t}fetch("/links/linklist.json").then(e=>e.json()).then(e=>renderlink(e));